INSERT INTO recipes (title, description, cooking_time, ingredients, method)
VALUES ('Lamb Biryani', 'Lamb biryani is an amazingly delicious Indian dish with tender chunks of lamb in layers of fluffy rice, fragrant spices and fried onions', 90, 'Lamb, basmati rice, onions, tomatoes, spices', '1. Marinate the lamb with spices. 2. Cook the rice separately. 3. Sauté onions and tomatoes. 4. Add marinated lamb and cook until tender. 5. Layer rice and lamb mixture. 6. Cook on low heat. 7. Garnish with fried onions. 8. Serve hot.');

INSERT INTO recipes (title, description, cooking_time, ingredients, method)
VALUES ('Mutton Biryani', 'Mutton Biryani is a popular and flavorful Indian rice dish made with tender chunks of mutton (goat meat) cooked with aromatic spices, fragrant basmati rice, and a blend of herbs', 120, 'Mutton, basmati rice, onions, tomatoes, spices, herbs', '1. Marinate the mutton with spices and herbs. 2. Cook the rice separately. 3. Sauté onions and tomatoes. 4. Add marinated mutton and cook until tender. 5. Layer rice and mutton mixture. 6. Cook on low heat. 7. Garnish with fresh herbs. 8. Serve hot.');

INSERT INTO recipes (title, description, cooking_time, ingredients, method)
VALUES ('Malabar biryani', 'Malabar biryani is a flavorful and aromatic rice dish from the Malabar region of Kerala, India, known for its unique blend of spices, tender meat, and fragrant basmati rice', 75, 'Chicken, basmati rice, spices, onions, tomatoes, cashews, raisins', '1. Marinate the chicken with spices. 2. Cook the rice separately. 3. Sauté onions, tomatoes, cashews, and raisins. 4. Add marinated chicken and cook until tender. 5. Layer rice and chicken mixture. 6. Cook on low heat. 7. Garnish with cashews and raisins. 8. Serve hot.');


